import React from "react";

const ITunes = (props) => {
    const array = props.itunes.map(itune => {
        return (
            <div>
                {((props.artist === itune.artistName) && (props.title === itune.trackName)) ? {array} : null}
                <audio controls>
                    <source src={itune.previewUrl} />
                </audio>
                <img src={itune.artworkUrl100} />
            </div>
        )})
    return (
        <div>
            {array}
            {/* {condition} */}


            {props.error ? 
                <div className="lyric-body">
                    Preview Not Found!
                </div> 
            : null}
        </div>
    )
}
export default ITunes
