import React from 'react';
import SongTitle from './components/SongTitle';
import Artists from './components/Artists';
import Lyrics from './components/Lyrics'; 
import axios from 'axios';
import './App.css';

class App extends React.Component {
  constructor(){
    super();
    this.state = {
      artist: "",
      title: "",
      fullLength: {}
    }
    // this.handleClick = this.handleClick.bind(this)
    this.handleArtistSearch = this.handleArtistSearch.bind(this)
    this.handleTitleSearch = this.handleTitleSearch.bind(this)
  }

  fetchData = async () => {
    const url = `https://api.lyrics.ovh/v1/maroon5/animals`
    const data = await axios.get(url)
    const { data: {fullLength} } = data
    this.setState({
      fullLength
    })
  }

  componentDidMount() {
    this.fetchData()
  }

  handleArtistSearch = (event) => {
    event.preventDefault()
    this.setState({
      artist: event.target.value
    })
  }
  handleTitleSearch = (event) => {
    event.preventDefault()
    this.setState({
      title: event.target.value
    })
  }
  // handleClick = (event) => {
  //   this.setState({
  //     lyrics: event.target.value
  //   })
  // }
  renderItem = () => {
    return this.state.fullLength.map(song => {
      const { lyrics } = song
      return (
        <p>{lyrics}</p>
      )
    })
  }

  render(){
    console.log("Lyrics", this.state.lyrics)
    return (
      <div className="App">
        <Artists artistName={this.state} onChange={this.handleArtistSearch} />
        <SongTitle songTitle={this.state} onChange={this.handleTitleSearch} />   
        <Lyrics lyrics={this.state} onClick={this.handleClick} />
        {this.renderItem()}
      </div>
    );
  }

}
export default App;
