import React from "react";
import './Lyrics.css'

const Lyrics = (props) => {
    const theLyrics = props.loading? <h1>Loading...</h1> : props.artistLyrics.lyrics
    return (
        <div  className="lyric-body">
            <div>
                {/* {props.loading ? <h1>loading...</h1> : props.artistLyrics.lyrics} */}
                {theLyrics}
                {props.error ? 
                <div>
                    Song Not Found!
                </div> 
            : null}
            </div>
        </div>
    )
}
export default Lyrics
