import React from 'react';
import axios from 'axios';
import './App.css';

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      // artist: [],
      // title: [],
      items: [],
      isLoaded: false
    }
  }

  fetchData = async () => {
    const url = 'https://api.lyrics.ovh/v1/Maroon 5/girls like you'
    const data = await axios.get(url)
    const { data: {items} } = data
    this.setState({
      isLoaded: true,
      items
    })
  }

  componentDidMount() {
    fetch('https://api.lyrics.ovh/v1/Maroon 5/girls like you')
  }

  render() {
    const { items, isLoaded } = this.state
    if (!isLoaded) {
      return <div>Loading . . .</div>
    } else {
      return (
        <div className="App">
          {items.map(item => (
            <div>
              <h1>{item.lyrics}</h1>
            </div>
          ))}

        </div>
      )
    }
  }
}
export default App;
