import React from 'react';
// import axios from 'axios';
import './App.css';

class App extends React.Component {
  constructor(){
    super();
    this.state = {
      artist: "",
      title: "",
      lyrics: "",
      isLoaded: false
    }
  }


  componentDidMount() {
    fetch(`https://api.lyrics.ovh/v1/${this.state.artist}/${this.state.title}`)
      .then(res => res.json())
      .then(json => {
        this.setState({
          isLoaded: true,
          lyrics: json,
        })
      })
    }

    render() {
      console.log(this.state.lyrics)
    const { isLoaded } = this.state
    if (!isLoaded) {
      return <div>Loading...</div>
    } else {
      return (
        <div>
          <div lyrics={this.state.lyrics}></div>
        </div>
      )
    }
  }
}
export default App;
