import React from "react";

const ITunes = (props) => {
    const array = props.items.map(item => {
        return (
            <div>
                <audio controls>
                    <source src={item.previewUrl} />
                </audio>
                <img src={item.artworkUrl100} />
            </div>
        )
    })
    const arrayFirst = props.loading? <h1>Loading...</h1> : array[0]
    return (
        <div>
            {arrayFirst}
        </div>
    )
}
export default ITunes
