import React from 'react'
import axios from 'axios'

class Itunes extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            items: []
        }
    }
    // fetchData = async () => {
    //     await axios.get(`https://itunes.apple.com/search?term=maroon 5&term=maps`)
    //     .then(res => {
    //         this.setState({
    //             items: res.data.results,
    //         })
    //     })
    //     .catch(error => {
    //         console.log(error)
    //     })
    // }
    // componentDidMount() {
    //     this.fetchData()
    // }
    componentDidMount() {
        fetch('https://itunes.apple.com/search?term=maroon 5&term=maps')
          .then(res => res.json())
          .then(json => {
            this.setState({
              isLoaded: true,
              items: json,
            })
          })
      }

    render() {
        const { items } = this.state
        return (
            <div className="text-white">
                {items.map(item => {
                    <h1>{item.wrapperType}</h1>
                })}
            </div>
        )
    }
}
export default Itunes
