import React from "react";
import './Lyrics.css'

const Lyrics = (props) => {
    return (
        <div  className="lyric-body">
            <div>
                {/* {props.loading ? <h1>loading...</h1> : props.artistLyrics.lyrics} */}
                {props.error ? 
                this.reset() &&
                <div className="lyric-body">
                    Song Not Found!
                </div> 
            : null}
                {props.artistLyrics.lyrics}
            </div>
        </div>
    )
}
export default Lyrics
