// import axios from 'axios';
import './App.css';

class App extends React.Component {
  constructor(){
    super();
    this.state = {
      // artist: [],
      // title: [],
      lyrics: {},
      isLoaded: false
    }
  }


  componentDidMount() {
    fetch('https://api.lyrics.ovh/v1/maroon 5/this love')
      .then(res => res.json())
      .then(json => {
        this.setState({
          isLoaded: true,
          lyrics: json,
        })
      })
    }

    render() {
