import React, {Component} from "react";
import axios from 'axios'
import Lyrics from '../Lyrics'
import FormInput from '../FormInput'
import Itunes from '../Itunes'

class LyricFinder extends Component {
    constructor() {
        super()
        this.state = {
            loading: false,
            artistLyrics: {},
            error: "",
            artist: "",
            title: "",
        }
        this.handleSubmit = this.handleSubmit.bind(this)
        this.handleChange = this.handleChange.bind(this)
        this.handleSecondChange = this.handleSecondChange.bind(this)
        this.clear = this.clear.bind(this) 

    }

    fetchData = async () => {
        this.setState({ loading: true, error: false })
        await axios.get(`https://api.lyrics.ovh/v1/${this.state.artist}/${this.state.title}`)
            .then(res => {
                this.setState({
                    loading: false,
                    artistLyrics: res.data
                })
            })
            .catch(error => {
                console.log(error)
                this.setState({error: true, loading: false})
            })
    }
    clear() {
        this.reset()
    }
    handleChange(event) {
        event.preventDefault()
        const {name, value} = event.target
        this.setState({[name]: value})
    }
    handleSecondChange(event) {
        event.preventDefault()
        const {name, value} = event.target
        this.setState({[name]: value})
    }
    handleSubmit(event) {
        event.preventDefault()
        this.fetchData()
        this.handleChange(event)
        this.handleSecondChange(event)
    }
    componentDidMount() {
        document.body.style.backgroundColor = "black"    
    }
    render() {
        const { error } = this.state
        return (
            <div>
                <FormInput
                    handleSubmit={this.handleSubmit}
                    handleChange={this.handleChange}
                    handleSecondChange={this.handleSecondChange}
                    artist={this.state.artist}
                    title={this.state.title}
                    clear={this.clear}
                />
                {error ?
                    <div
                        className="lyric-body">
                        Song Not Found!
                        </div>
                    : null
                }
                <Lyrics
                    artistLyrics={this.state.artistLyrics}
                    loading={this.state.loading}
                />
                <Itunes 
                    artist={this.state.artist}
                    title={this.state.title}
                    parentCallback = {this.handleSubmit}
                />
            </div>
        )
    }
}

export default LyricFinder
