import React from 'react'
import axios from 'axios'

class Itunes extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            data: {},
            loading: false
        }
    }
    fetchData = async () => {
        await axios.get(`https://itunes.apple.com/search?term=maroon 5&term=maps`)
        .then(res => {
            this.setState({
                data: res.data,
                loading: true,
            })
        })
        .catch(error => {
            console.log(error)
        })
    }
    componentDidMount() {
        this.fetchData()
    }

    render() {
        const { data, loading } = this.state
        console.log(data.wrapperType)
        return (
            <div className="text-white">
                    <h2>{loading? <h1>Loading...</h1> : data.trackName}</h2>
            </div>
        )
    }
}
export default Itunes
