import React from "react";

const Lyrics = (props) => {
    if (props.loading){
        return (<h1>Loading</h1>)
    } else if (!props.error) {
    return (<h1>{props.error}</h1>)
    } else {
    return (
        <div>
            <h1>{props.artistLyrics.lyrics}</h1>
            {/* <div>{props.loading ? <h1>loading...</h1> : props.artistLyrics.lyrics}
            {props.error? props.error : null}</div> */}
        </div>
    )}
}
export default Lyrics
