import axios from 'axios';
import './App.css';

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      artist: [],
      title: [],
      item: []
    }
  }

  fetchData = async () => {
    const url = `https://api.lyrics.ovh/v1/drake bell/hollywood girl`
    const data = await axios.get(url)
    const { data: {item} } = data
    console.log(data)
    this.setState({
      item
    })
  }

  componentDidMount() {
    this.fetchData()
  }



  render() {
    console.log("Lyrics", this.state.item)
