import React from "react";
import './Lyrics.css'

const Lyrics = (props) => {
    const theLyrics = props.loading? <h1>Loading...</h1> : props.artistLyrics.lyrics
    {props.error ? 
     <div>
        Song Not Found!
    </div> 
: null}
    return (
        <div  className="lyric-body">
            <div>
                {theLyrics}
            </div>
        </div>
    )
}
export default Lyrics
