import React, {Component} from "react"

class App extends Component {
    constructor() {
        super()
        this.state = {
            loading: false,
            artistName: {},
            error: ""
        }
    }

    componentDidMount() {
        this.setState({loading: true})
        fetch('https://api.lyrics.ovh/v1/coco/remember me')
            .then(response => response.json())
            .then(data => {
                this.setState({
                    loading: false,
                    artistName: data
                })
            })
            .catch(error => {
                console.log(error)
                this.setState({error: "Lyrics not Available"})
            })
    }


    render() {
        const text = this.state.loading ? "loading..." : this.state.artistName.lyrics
        return (
            <div>
                <p>{text}</p>
                { this.state.error ? <div>{this.state.error}</div> : null }
            </div>
        )
    }
}

export default App
