import React, {Component} from "react";
import axios from 'axios'

class App extends Component {
    constructor() {
        super()
        this.state = {
            loading: false,
            artistLyrics: {},
            error: "",
            artist: "",
            title: ""
        }
        this.handleSubmit = this.handleSubmit.bind(this)
        this.handleChange = this.handleChange.bind(this)
        this.handleSecondChange = this.handleSecondChange.bind(this)

    }

    fetchData = async () => {
        this.setState({ loading: true })
        await axios.get(`https://api.lyrics.ovh/v1/${this.state.artist}/${this.state.title}`)
            .then(res => {
                console.log(res)
                this.setState({
                    loading: false,
                    artistLyrics: res.data
                })
            })
            .catch(error => {
                console.log(error)
                // this.setState({ error: "Lyrics not Available" })
            })
    }
    handleChange(event) {
        event.preventDefault()
        const {name, value} = event.target
        this.setState({[name]: value})
        this.fetchData()
    }
    handleSecondChange(event) {
        event.preventDefault()
        const {name, value} = event.target
        this.setState({[name]: value})
        this.fetchData()
    }
    handleSubmit(event) {
        event.preventDefault()
        this.fetchData()
        this.handleChange(event)
        this.handleSecondChange(event)
    }




    render() {
        const text = this.state.loading ? "loading..." : this.state.artistLyrics.lyrics
        // const errorMsg = this.state.error ? this.state.error : null
        return (
            <div>
                {/* <p>{errorMsg}</p> */}
                <div>
                    <form onSubmit={this.handleSubmit}>
                        <label>
                            <input 
                                type="text" 
                                value={this.state.artist} 
                                name="artist"
                                placeholder="Artist"
                                onChange={this.handleChange} 
                            />
                        </label>
                        <label>
                            <input 
                                type="text" 
                                value={this.state.title} 
                                name="title"
                                placeholder="Title"
                                onChange={this.handleSecondChange} 
                            />
                        </label>
                        <button>Submit</button>
                    </form>
                    <h1>{this.state.artist}</h1>
                    <h1>{this.state.title}</h1>
                    <p>{text}</p>
                </div>
            </div>
        )
    }
}

export default App
