import React from 'react'
import axios from 'axios'

class Itunes extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            data: {}
        }
    }
    componentDidMount() {
        fetch(`https://itunes.apple.com/search?term=${this.props.artist}&term=${this.props.title}`)
        .then(res => res.json())
        .then(json => {
            this.setState({
                data: json,
            })
        })
        .catch(error => {
            console.log(error)
        })
    }

    render() {
        console.log(this.state.data)
        return (
            <div>{this.state.data.wrapperType}</div>
        )
    }
}
export default Itunes
