import React, {Component} from "react";
import axios from 'axios'

class App extends Component {
    constructor() {
        super()
        this.state = {
            loading: false,
            artistLyrics: {},
            error: "",
            artist: "",
            title: ""
        }
        this.handleChange = this.handleChange.bind(this)
        this.handleSecondChange = this.handleSecondChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
    }

    fetchData = async () => {
        this.setState({ loading: true })
        await axios.get(`https://api.lyrics.ovh/v1/${this.state.artist}/${this.state.title}`)
            // .then(response => response.json())
            .then(res => {
                console.log(res)
                this.setState({
                    loading: false,
                    artistLyrics: res.data
                })
            })
            .catch(error => {
                console.log(error)
                // this.setState({ error: "Lyrics not Available" })
            })
    }
    handleChange(event) {
        this.setState({artist: event.target.value})
        this.fetchData()
    }
    handleSecondChange(event) {
        this.setState({title: event.target.value})
        this.fetchData()
    }
    handleSubmit() {
        // this.fetchData()
        this.handleChange()
        this.handleSecondChange()
    }
    // componentDidMount() {
    //     this.handleSubmit()
    //     this.handleChange()
    //     this.handleSecondChange()
    //     this.fetchData()
    // }


    render() {
        console.log(this.state.artistLyrics.lyrics)
        const { error } = this.state
        const text = this.state.loading ? "loading..." : this.state.artistLyrics.lyrics
        return (
            <div>
                <p>{text}</p>
                { error ? <p>{error}</p> : null }
                <div>
                    <form>
                        <label>
                            <input type="text" value={this.state.artist} onChange={this.handleChange} />
                        </label>
                        <label>
                            <input type="text" value={this.state.title} onChange={this.handleSecondChange} />
                        </label>
                        <input type="submit" value="Submit" onSubmit={this.handleSubmit} />
                    </form>
                </div>
            </div>
        )
    }
}

export default App
