import React from 'react'
import axios from 'axios'

class Itunes extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            result: []
        }
    }
    // fetchData = async () => {
    //     await axios.get(`https://itunes.apple.com/search?term=${this.props.artist}&term=${this.props.title}`)
    //     .then(res => {
    //         this.setState({
    //             data: res.data,
    //         })
    //     })
    //     .catch(error => {
    //         console.log(error)
    //     })
    // }
    // componentDidMount() {
    //     this.fetchData()
    // }

    componentDidMount() {
        fetch('https://itunes.apple.com/search?term=maroon 5&term=maps')
          .then(res => res.json())
          .then(json => {
            this.setState({
              result: json,
            })
          })
      }
    render() {
        const { result } = this.state
        return (
            <div>{result.map(item => (
            <div>{item.trackCensoredName}</div>
            ))}</div>
        )
    }
}
export default Itunes
